function doLoadOnFollowing(){initOnHub(),getFollowingBlogsFromServer()}function showFollowingBlogs(e){let o=document.getElementById("following-container");for(;o.firstChild;)o.removeChild(o.firstChild);for(let n=0;n<e.count;n++){let l=(new Blog).fromJson(e.blogs[n]),t=document.createElement("div");t.className="following-container rounded",t.id="blog-"+l.id,t.style.backgroundColor=l.backgroundColor,o.appendChild(t);let i=document.createElement("img");i.className="following-blog-icon rounded-left",i.src=l.iconImage.url,i.alt=l.iconImage.name,t.appendChild(i);let r=document.createElement("a");r.className="following-blog-link text-link-color underline-solid",r.href=l.completeUrl,r.target="_blank",r.innerHTML=l.baseUrl,t.appendChild(r)}}function getFollowingBlogsFromServer(){$.ajax({url:"https://api.startapped.com/v1/relation/get/following",headers:{"Content-Type":"application/json",Authorization_Access:getCredentials().access,Authorization_Refresh:getCredentials().refresh},method:"POST",dataType:"json",data:JSON.stringify({}),success:function(e){showFollowingBlogs(e)},error:function(e,o,n){showSnackbar(JSON.parse(e.responseText).message)}})}