function signUp(){let e={email:document.getElementById("sign-up-email").value,username:document.getElementById("sign-up-username").value,password:document.getElementById("sign-up-password").value,birthday:document.getElementById("sign-up-birthday").value,gcap:grecaptcha.getResponse(1)};$.ajax({url:"https://api.startapped.com/v1/account/register",headers:{"Content-Type":"application/json"},method:"POST",dataType:"json",data:JSON.stringify(e),success:function(e){saveCredentials(e.credentials),window.location.replace("/account")},error:function(e,t,a){showSnackbar(JSON.parse(e.responseText).message),grecaptcha.reset()}})}function checkPasswordValidity(e){e.value!==document.getElementById("sign-up-password").value?e.setCustomValidity("Passwords must match!"):e.setCustomValidity("")}